$('#login-button').click(function (event) {
	var userName=document.getElementById("userName").value;  
    if(userName=="茶迪生日快乐！"){
		event.preventDefault();
		$('form').fadeOut(500);
		$('.wrapper').addClass('form-success');
		setTimeout(function(){location.href="BirthdayCake.html";},2000);
	}
	else{
		alert("你说错了！");
	}
});
